awk 'BEGIN {FS=","; count=0; min=length($4)-2000; max=length($4)-2000}; {for (i=1; i<=NF; i++) {sum[i] += length($4)-2000; sumsq[i] += (length($4)-2000)^2; if (length($4)-2000 > max) max = length($4)-2000; if (length($4)-2000 < min) min = length($4)-2000}} END {for (i=1; i<=NF; i++) {avg = sum[i]/NR; std=sqrt((sumsq[i]-sum[i]^2/NR)/NR)}} END {if (min < 0) min = 0} END {print avg; print std; print min; print max}' all_genes.csv >> summary_stats.txt
